logic [15:0] data_next;
logic [1:0] respProc_PROC_STATE;
logic [1:0] respProc_PROC_STATE_next;

// Next-state combinational logic
always_comb begin : respProc_comb     // bput_test.h:62:5
    respProc_func;
end
function void respProc_func;
    logic [15:0] TMP_0;
    data_next = data0;
    fifo_get_req = '0;
    resp_sync_data_next = resp_sync_data;
    resp_sync_req_next = resp_sync_req;
    respProc_PROC_STATE_next = respProc_PROC_STATE;
    
    case (respProc_PROC_STATE)
        0: begin
            // Call b_get() begin
            if (!fifo_out_valid)
            begin
                respProc_PROC_STATE_next = 0; return;    // sct_fifo.h:182:28;
            end
            fifo_get_req = 1;
            TMP_0 = fifo_data_out;
            // Call b_get() end
            data_next = TMP_0;
            // Call b_put() begin
            if (!resp_core_ready)
            begin
                respProc_PROC_STATE_next = 2; return;    // sct_initiator.h:182:37;
            end
            resp_sync_req_next = !resp_sync_req;
            resp_sync_data_next = data_next;
            // Call b_put() end
            respProc_PROC_STATE_next = 0; return;    // bput_test.h:72:13;
        end
        2: begin
            // Call b_put() begin
            if (!resp_core_ready)
            begin
                respProc_PROC_STATE_next = 2; return;    // sct_initiator.h:182:37;
            end
            resp_sync_req_next = !resp_sync_req;
            resp_sync_data_next = data_next;
            // Call b_put() end
            respProc_PROC_STATE_next = 0; return;    // bput_test.h:72:13;
        end
    endcase
endfunction

// Synchronous register update
always_ff @(posedge resp_clk or negedge nrst) 
begin : respProc_ff
    if ( ~nrst ) begin
        // Call reset_put() begin
        resp_sync_req <= 0;
        resp_sync_data <= 0;
        // Call reset_put() end
        respProc_PROC_STATE <= 0;    // bput_test.h:65:9;
    end
    else begin
        resp_sync_req <= resp_sync_req_next;
        resp_sync_data <= resp_sync_data_next;
        data0 <= data_next;
        respProc_PROC_STATE <= respProc_PROC_STATE_next;
    end
end

endmodule


